CREATE TABLE IF NOT EXISTS mods_buffer_skill (
  `skill_id` int(11) NOT NULL,
  `skill_level` int(11) NOT NULL,
  `skill_fee_id` int(11) NOT NULL DEFAULT '0',
  `skill_fee_amount` bigint(20) NOT NULL DEFAULT '0',
  `skill_comp` varchar(35) DEFAULT NULL,
  `skill_desc` longtext,
  `skill_icon` varchar(200) DEFAULT NULL,
  `skill_group` varchar(10) NOT NULL DEFAULT 'None',
  `skill_donator` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`skill_id`,`skill_level`)
);


CREATE TABLE IF NOT EXISTS mods_buffer_scheme (
  scheme_id INT NOT NULL AUTO_INCREMENT,
  player_id INT NOT NULL DEFAULT 0,
  scheme_name VARCHAR(25) NULL,
  scheme_type INT NOT NULL DEFAULT 0, -- 0 = Character 1 = Pet/Summon
  PRIMARY KEY(scheme_id)
);


CREATE TABLE IF NOT EXISTS mods_buffer_content (
  skill_level INT NOT NULL,
  skill_id INT NOT NULL,
  scheme_id INT NOT NULL,
  PRIMARY KEY(skill_level, skill_id, scheme_id),
  FOREIGN KEY(skill_id, skill_level)
    REFERENCES mods_buffer_skill(skill_id, skill_level)
      ON DELETE CASCADE
      ON UPDATE CASCADE,
  FOREIGN KEY(scheme_id)
    REFERENCES mods_buffer_scheme(scheme_id)
      ON DELETE CASCADE
      ON UPDATE CASCADE
);

