package l2rt.gameserver.network.serverpackets;// Автоматический поиск партииpublic class ExCallToChangeClass extends L2GameServerPacket{    private static final String _S__FE_FD_ExCallToChangeClass = "[S] FE:FD - ExCallToChangeClass";		private int _newClass;		public ExCallToChangeClass(int newClass)	{		_newClass = newClass;	}	    @Override	protected void writeImpl()	{		writeC(EXTENDED_PACKET);		writeH(0xfd);        writeD(_newClass); // Новая Awaking профа        writeD(0x01); // хз, при 0 ничего не видно	}    @Override	public String getType()	{		return _S__FE_FD_ExCallToChangeClass;	}}